mapper.select:
select user_id, name, birth, address, pn, status from t_user where
user_id=#{user_id} and name=#{name} and birth=#{birth} and
address=#{address} and pn=#{pn} and status=#{status}

mapper.update:

		update t_user set 
		pw=#{password}, 
		status="ACTV", 
  		updated=#{formattedDate} 
		where user_id=#{userId}
